import { Pipe, PipeTransform } from '@angular/core';
import { Recipe } from 'src/modals/recipe';

@Pipe({
  name: 'filter'
})
export class FilterPipe implements PipeTransform {
  transform(recipes: Recipe[], filterTerm: string): Recipe[] {
    if (!filterTerm) {
      return recipes;
    }

    return recipes.filter((recipe: Recipe) => {
      const searchFields = [
        recipe.name,
        recipe.category,
        recipe.description
      ];

      return searchFields.some((field: string) =>
        field.toLowerCase().includes(filterTerm.toLowerCase())
      );
    });
  }
}
