import { Component } from '@angular/core';

@Component({
  selector: 'app-booktable',
  templateUrl: './booktable.component.html',
  styleUrls: ['./booktable.component.css']
})
export class BooktableComponent {

}
<div class="container-fluid booktable text-white">
    <div class="container py-5 booktable_desc">
        <h1>An Ambient <br> Dining Experience</h1>
        <h5>Join us at the table as you dine for the perfect meal. 
            We treat all of our customers with utmost care and service.
            <br>Have a toast and enjoy our fine drinks while you're at it.
        </h5>
        <button class="btn btn-danger">Book a Table</button>
    </div>
</div>
